mapper.select:
select * from 
(select rownum rn, temp.* from (select seq,id,name,email,subject,content,ref,lev,step,pseq,reply,hit,to_char(logtime,'YYYY.MM.DD') as logtime from board order by ref desc, step asc) temp) 
where rn between #{startNum} and #{endNum}

select * from 
(select rownum rn, temp.* from (select seq,id,name,email,subject,content,ref,lev,step,pseq,reply,hit,to_char(logtime,'YYYY.MM.DD') as logtime from board where ${searchOp} like '%'||#{searchWord}||'%' order by ref desc, step asc) temp) 
where rn between #{startNum} and #{endNum}
